var scale = 0.049;

function drawConstellations(d,s,attr){

	if(typeof s==="number") scale = s;

	if(!attr) attr = {};
	if(typeof attr.showboundary!=="boolean") attr.showboundary = true;
	if(typeof attr.showlines!=="boolean") attr.showlines = false;
	if(typeof attr.showstars!=="boolean") attr.showstars = false;
	if(!attr.grid) attr.grid = [11,8];
	var w = (typeof attr.w==="number") ? attr.w : $('#holder').width() - 30;
	var h = (typeof attr.h==="number") ? attr.h : w*(attr.grid[1]/attr.grid[0]);
	var mid = {'x': w/2,'y':h/2};
	attr.w = w;
	attr.h = h;

	if(!attr.paper){
		var el = document.getElementById('holder');
		el.innerHTML = '';
		el.style = 'width:'+w+'px;height:'+h+'px;';
		attr.paper = Raphael("holder", w, h);
		$('#holder svg').attr('id','canvas');
		var svg = attr.paper.set();
		var bound = attr.paper.set();
	}
	areasvg = attr.paper.set();

	var padd = 30;

	$('#saver').on('click',function(e){
		e.preventDefault();
		savesvg();
	});

	for(var i = 0; i < d.length; i++){
		drawConstellation(d[i][0],padd+(w-padd*2)*d[i][1],padd+(h-padd*2)*d[i][2],attr);
	}
		
	return attr;
}
function drawConstellation(abbr,xoff,yoff,attr){

	if(!attr) attr = {};
	if(!attr.colour) attr.colour = 'red';
	var paper = attr.paper;

	var c = -1;
	var constellation = [];
	for(var j = 0; j < lines.length; j++){
		if(lines[j][0]==abbr) c = j;
	}
	// We haven't found it so quit
	if(c < 0) return;

	for(var j = 0; j < boundaries.length; j++){
		if(boundaries[j][0]==abbr) constellation = boundaries[j];
	}
	
	var posa, posb, a, b, l, d,  i, j, ra,dc,dra,ddc,b3;

	// Keys defining a line in both directions
	var atob = "";
	var btoa = "";
	var path = "";
	var n = 5;
	// Create a new array of points
	var points = new Array();

	if(attr.showboundary){
		for(l = 1; l < constellation.length; l+=2){
			b = [constellation[l],constellation[l+1]];
			if(a){
				atob = a[0]+','+a[1]+'-'+b[0]+','+b[1];
				btoa = b[0]+','+b[1]+'-'+a[0]+','+a[1];
			}
			if(l > 1){
				ra = (b[0]-a[0])%360;
				if(ra > 180) ra = ra-360;
				if(ra < -180) ra = ra+360;
				dc = (b[1]-a[1]);
	
				// If we've already done this line we'll only calculate 
				// two points on the line otherwise we'll do 5
				n = 5;
				if(ra/2 > n) n = parseInt(ra);
				if(dc/2 > n) n = parseInt(dc);
				
				dra = ra/n;
				ddc = dc/n;
				
				for(var i = 1; i <= n; i++){
					ra = a[0]+(i*dra);
					if(ra < 0) ra += 360;
					dc = a[1]+(i*ddc);
					// Convert to J2000
					d = sky.fk1tofk5(ra*sky.d2r,dc*sky.d2r);
					points.push([d[0],d[1],true]);
				}
			}
			a = b;
		}
	}
	posa = null;

	sky.fov = 10;

	sky.ra_off = lines[c][1]*sky.d2r;
	sky.dc_off = lines[c][2]*sky.d2r;

	function scaleX(x){ return (x*scale + xoff).toFixed(2); }
	function scaleY(y){ return (y*scale + yoff).toFixed(2); }

	if(attr.showboundary){
		path = "";
		// Now loop over joining the points
		for(i = 0; i <= points.length; i++){
			j = (i == points.length) ? 0 : i;
			posb = sky.radec2xy(points[j][0],points[j][1]);
			if(posa && points[j][2]){
	
				if(!sky.isPointBad(posa) && !sky.isPointBad(posb)){
					path += (path=="" ? "M": 'L')+scaleX(posb.x)+','+scaleY(posb.y);
				}
			}
			posa = posb;
		}
		path += "z";
		// Draw constellation boundaries
		areasvg.push(paper.path(path).attr({'fill':colours[attr.colour][3],'stroke':colours[attr.colour][2],'stroke-width':0,'opacity':0.7}));
		areasvg[areasvg.length-1].node.id = abbr;
		areasvg[areasvg.length-1].click(function(e){
			for(var i = 0; i < areasvg.length; i++) areasvg[i].attr({'fill':colours[attr.colour][3],'opacity':0.7});
			if(this.attr('fill')==colours[attr.colour][3]) this.attr({'fill':colours[attr.colour][0],'opacity':1})
			this.toFront()
		})
	}


	if(attr.showlines){
		// Draw the constellation lines
		if(!sky.hipparcos) sky.hipparcos = new Array();
		path = "";
		for(l = 3; l < lines[c].length; l+=2){
			a = -1;
			b = -1;
			idx1 = ''+lines[c][l]+'';
			idx2 = ''+lines[c][l+1]+'';
			if(!sky.hipparcos[idx1]){
				for(s = 0; s < sky.stars.length; s++){
					if(sky.stars[s][0] == lines[c][l]){
						sky.hipparcos[idx1] = s;
						break;
					}
				}
			}
			if(!sky.hipparcos[idx2]){
				for(s = 0; s < sky.stars.length; s++){
					if(sky.stars[s][0] == lines[c][l+1]){
						sky.hipparcos[idx2] = s;
						break;
					}
				}
			}
			a = sky.hipparcos[idx1];
			b = sky.hipparcos[idx2];
			if(a >= 0 && b >= 0 && a < sky.stars.length && b < sky.stars.length){
				posa = sky.radec2xy(sky.stars[a][2], sky.stars[a][3]);
				posb = sky.radec2xy(sky.stars[b][2], sky.stars[b][3]);
				if(sky.isVisible(posa.el) && sky.isVisible(posb.el)){
					if(!sky.isPointBad(posa) && !sky.isPointBad(posb)){
						path += 'M'+scaleX(posa.x)+','+scaleY(posa.y)+'L'+scaleX(posb.x)+','+scaleY(posb.y);
					}
				}
			}
		}
		paper.path(path).attr({'stroke':colours[attr.colour][0],'stroke-width':1});
	}

	if(attr.showstars){
		// Draw the stars
		for(i = 0; i < sky.stars.length; i++){
			if(sky.stars[i][1] < 5){
				mag = sky.stars[i][1];
				p = sky.radec2xy(sky.stars[i][2], sky.stars[i][3]);
	
				if(sky.isVisible(p.el) && !isNaN(p.x) && !sky.isPointBad(p)){
					
					con = findConstellation(sky.stars[i][2]*sky.r2d, sky.stars[i][3]*sky.r2d)
					if(con==abbr){
						d = 2*Math.max(3-mag/2.1, 0.5);
						paper.circle(scaleX(p.x),scaleY(p.y),d*12*scale).attr({'fill':colours['yellow'][0],'stroke':0});			
					}
				}
			}	
		}
	}

	return;
}

